import React, { useEffect, useState } from 'react'
import styles from "./styles.module.css";
import _, { set } from 'lodash';
import { Button, Card, Divider, Icon, Image, Placeholder } from 'semantic-ui-react';
import { Link } from "react-router-dom";

const Main = () => {
	const handleLogout = () => {
		localStorage.removeItem("token");
		window.location.reload();
	};

  const [loading, setLoading] = useState(false);
  const [voteForA, setVoteForA] = useState(0);
  const [voteForB, setVoteForB] = useState(0);
  const [voted, setVoted] = useState(false);
  const [open, setOpen] = useState(false);
  const [openVoted, setOpenVoted] = useState(false);

	const cards =[
		{
		  "id": 1,
		  "dishName": "Lasagne",
		  "description": "Breaded fried chicken with waffles, and a side of maple syrup.",
		  "image": "https://loremflickr.com/300/300/food"
		},
		{
		  "id": 2,
		  "dishName": "Pho",
		  "description": "Three eggs with cilantro, tomatoes, onions, avocados and melted Emmental cheese. With a side of roasted potatoes, and your choice of toast or croissant.",
		  "image": "https://loremflickr.com/300/300/food"
		},
	  ]

	//   useEffect(()=>{
	// 	  fetch('https://raw.githubusercontent.com/syook/react-dishpoll/main/db.json')
	// 	       .then(response =>response.json())
	// 		   .then(data=>setCards(data))

	//   })


	 const handleLoadingClick = () => {
		setLoading(true)

		setTimeout(() => {
		  setLoading(false)
		}, 3000)
	  }



	return (
		<div className={styles.main_container}>
			<nav className={styles.navbar}>
				<h1>Syook</h1>
				<button className={styles.white_btn} onClick={handleLogout}>
					Logout
				</button>
			</nav>

			<div>
        {voteForA} - {voteForB}
      </div>
      <Divider />

      <Modal
        basic
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size="small"
        // trigger={<Button>Basic Modal</Button>}
      >
        <Header icon>
          <Icon name="archive" />
          You are about to vote for this party
        </Header>
        <Modal.Content>
          <p>Are you sure this party is the best one?</p>
        </Modal.Content>
        <Modal.Actions>
          <Button basic color="red" inverted onClick={() => setOpen(false)}>
            <Icon name="remove" /> No
          </Button>
          <Button
            color="green"
            inverted
            onClick={() => [setOpen(false), submitVote(), setOpenVoted(true)]}
          >
            <Icon name="checkmark" /> Yes
          </Button>
        </Modal.Actions>
      </Modal>
      <Modal
        basic
        onClose={() => setOpenVoted(false)}
        onOpen={() => setOpenVoted(true)}
        open={openVoted}
        size="small"
        // trigger={<Button>Basic Modal</Button>}
      >
        <Header icon>
          <Icon name="archive" />
          You have voted!
        </Header>
        <Modal.Content>
          <h4>Thanks! Want to see the result now?</h4>
        </Modal.Content>
        <Modal.Actions>
          <Button
            basic
            color="red"
            inverted
            onClick={() => [
              setOpenVoted(false),
              setOpen(false),
              window.location.reload(),
            ]}
          >
            <Icon name="remove" /> No
          </Button>
          <Link to="/result">
            <Button
              color="green"
              inverted
              onClick={() => [setOpenVoted(false), setOpen(false)]}
            >
              <Icon name="checkmark" /> Yes
            </Button>
          </Link>
        </Modal.Actions>
      </Modal>

		</div>
	);
};

export default Main;
